var priceForOneLevel=70;document.querySelector("#product_card_form_1");for(var i=0;i<11;i++){var $productCards=document.querySelectorAll("#product_card_form_".concat(String(i+1)));if($productCards===1){addingSliderFunctionality($productCards)}else{$productCards.forEach(function($item){addingSliderFunctionality($item)})}}function addingSliderFunctionality($form){var $input=$form.querySelector(".product-card__levels-input");var $output=$form.querySelector(".product-card__levels-output");var $value=$form.querySelector(".product-card__price-value");$input.addEventListener("input",function(){$output.textContent=$input.value;$value.textContent=$input.value*priceForOneLevel})}// REVIEW 
//Review slider
var $reviewContentContainer=document.querySelector(".reviews__content-container");var $reviewsArray=$reviewContentContainer.querySelectorAll(".review");var countReview=$reviewsArray.length;var reviewWidth=$reviewsArray[0].clientWidth;var countReviewInContainer=Math.floor($reviewContentContainer.clientWidth/reviewWidth);var reviewGap=$reviewContentContainer.clientWidth%reviewWidth/(countReviewInContainer-1);window.addEventListener("resize",function(){reviewWidth=$reviewsArray[0].clientWidth;countReviewInContainer=Math.floor($reviewContentContainer.clientWidth/reviewWidth);reviewGap=$reviewContentContainer.clientWidth%reviewWidth/(countReviewInContainer-1)});$reviewsArray[0].style.left="-".concat(reviewWidth+reviewGap,"px");$reviewsArray[1].style.left="".concat(0,"px");for(var _i=0;_i<countReviewInContainer-1;_i++){$reviewsArray[_i+2].style.left="".concat((reviewWidth+reviewGap)*(_i+1),"px")}for(var _i2=0;_i2<countReview-countReviewInContainer-1;_i2++){$reviewsArray[countReviewInContainer+_i2+1].style.left="".concat((reviewWidth+reviewGap)*countReviewInContainer,"px")}setInterval(slide,5000);function slide(){for(var _i3=0;_i3<countReviewInContainer+1;_i3++){$reviewsArray[_i3+1].style.left="".concat(parseInt($reviewsArray[_i3+1].style.left)-(reviewWidth+reviewGap),"px")}$reviewsArray[0].style.left="".concat((reviewWidth+reviewGap)*countReviewInContainer,"px");$reviewContentContainer.append($reviewsArray[0]);$reviewsArray=$reviewContentContainer.querySelectorAll(".review")}