var priceForOneLevelInRub=70;var priceForOneLevelInUsd=1;for(var i=0;i<document.querySelectorAll(".product .product-card").length;i++){var $productCards=document.querySelectorAll("#product_card_form_".concat(String(i+1)));$productCards.forEach(function($item){addingSliderFunctionality($item)})}function addingSliderFunctionality($form){var $input=$form.querySelector(".product-card__levels-input");var $output=$form.querySelector(".product-card__levels-output");var $value=$form.querySelector(".product-card__price-value");$value.textContent=currentLanguage==="en"?$input.value*priceForOneLevelInUsd:$input.value*priceForOneLevelInRub;$headerLanguage.addEventListener("click",function(){$value.textContent=currentLanguage==="en"?$input.value*priceForOneLevelInUsd:$input.value*priceForOneLevelInRub});$input.addEventListener("input",function(){$output.textContent=$input.value;$value.textContent=currentLanguage==="en"?$input.value*priceForOneLevelInUsd:$input.value*priceForOneLevelInRub})}// REVIEW 
