// Adding slider functionality
var priceForOneLevel=70;document.querySelector("#product_card_form_1");for(var i=0;i<11;i++){var $productCards=document.querySelectorAll("#product_card_form_".concat(String(i+1)));if($productCards===1){addingSliderFunctionality($productCards)}else{$productCards.forEach(function($item){addingSliderFunctionality($item)})}}function addingSliderFunctionality($form){var $input=$form.querySelector(".product-card__levels-input");var $output=$form.querySelector(".product-card__levels-output");var $value=$form.querySelector(".product-card__price-value");$input.addEventListener("input",function(){$output.textContent=$input.value;$value.textContent=$input.value*priceForOneLevel})}console.log(1234);